(function($){"use strict";$('a.js-scroll-trigger[href*="#"]:not([href="#"])').click(function(){if(location.pathname.replace(/^\//,'')==this.pathname.replace(/^\//,'')&&location.hostname==this.hostname){var target=$(this.hash);target=target.length?target:$('[name='+this.hash.slice(1)+']');if(target.length){$('html, body').animate({scrollTop:(target.offset().top-70)},1000,"easeInOutExpo");return false;}}});$('.js-scroll-trigger').click(function(){$('.navbar-collapse').collapse('hide');});$('body').scrollspy({target:'#mainNav',offset:100});var navbarCollapse=function(){if($("#mainNav").offset()!=undefined&&$("#mainNav").offset().top>100){$("#mainNav").addClass("navbar-shrink");}else{$("#mainNav").removeClass("navbar-shrink");}};navbarCollapse();$(window).scroll(navbarCollapse);$(".slider").slick({autoplay:true,mobileFirst:true,lazyLoad:"ondemand",arrows:true,dots:true});$(".review-slider").slick({autoplay:true,autoplaySpeed:5000,mobileFirst:true,arrows:false,dots:true});["red","yellow","green","blue","purple"].forEach(function(room){var disp=".display".concat(room);var slicknav=".nav".concat(room);$(disp).slick({mobileFirst:true,lazyLoad:"ondemand",arrows:false,slidesToShow:1,slidesToScroll:1});$(slicknav).slick({mobileFirst:true,lazyLoad:"ondemand",arrows:true,slidesToShow:4,slidesToScroll:1,focusOnSelect:true,asNavFor:disp});});})(jQuery);